html,
body {
  width: 100%;
  height: 100%;
}

body {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

button {
  margin: 20px;
}

#field {
  width: 400px;
  height: 400px;
  display: flex;
  flex-wrap: wrap;
}

.cell {
  width: 38px;
  height: 38px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid green;
  background-image: url("grooter.gif");
}

.bgblack {
  background-color: black;
  color: white;
}

